import React,{useState} from 'react';




const App = () =>{

    let [fullname,setfullname]=useState({
    fname:'',
    lname:'',
    });	
    

                                     
    const inputEvent = (event) => {
          console.log(event.target.name );
      console.log(event.target.value);
      const name1 = event.target.name;
      const value = event.target.value;
      console.log(name1);
        const name = event.target.name;
      setfullname((prevalue) => {
     // console.log(prevalue);
     if(name1==='fname'){
      return {
      fname:value,
      lname:prevalue.lname
      }
     }
     else{
       if(name1==='lname'){
      return {
      fname:value,
      lname:prevalue.lname
      }
     }
     }
    
      });
    
    }

const onSubmit = (event) => {
event.preventDefault();
     
      alert('formsubmitted');
    };


	return (
    <>
      <form onSubmit={onSubmit} action='#'>
          <h1>Your Full Name -  </h1>
          <input type="text" placeholder='Enter Your First Name' value={fullname.fname} onChange={inputEvent} name='fname'/>
          <input type="text" placeholder='Enter Your Last Name' value={fullname.lname} onChange={inputEvent} name='lname'/>
           <input type="text" placeholder='Enter Your Email'  value={fullname.email} onChange={inputEvent} name='email'/>
          <button type='submit'>Submit</button>
      </form>
    </>
	)
};
export default App;
