   const Main = () => {
 let [state,setState]=useState('vindo');
 let [fullname,setfullname]=useState();
  let [stateto,lname]=useState();
  const getValto = (event1) =>{

       setState(event1.target.value);
    }
 const getVal = (event) =>{

       setState(event.target.value);
    }
    const data = (event) =>{
    	event.preventDefault();
setfullname(state);

    }
   	return(
       <>
       <form onSubmit={data}>
        <h1>hello...{fullname}</h1>
        <input type='text' placeholder='Enter Your Name...' onChange={getVal} val={state}/>
         <input type='text' placeholder='Enter Your lname Name...' onChange={getValto} val={stateto}/>
        <button type='submit'>Click me</button>
        </form>
       </>
   		)
   }

export default Main;
