     let [lname,setState1]=useState('');
       let [fullname,setState3]=useState('');	
    const Fname = (event) =>{
      setState(event.target.value);
    }
    const Lname = (event1) =>{
setState1(event1.target.value);
}
const onSubmit = (event) =>{
event.preventDefault();
setState3(fname);

}
	return(
    <>
      <form onSubmit={onSubmit}>
          <h1>Your Full Name - {fullname}  </h1>
          <input type="text" placeholder='Enter Your First Name' onChange={Fname}/>
          <input type="text" placeholder='Enter Your Last Name' onChange={Lname}/>
          <button type='submit'>Submit</button>
      </form>
    </>
	)
}
