import React,{useState} from 'react';




const App = () =>{

    let [fullname,setfullname]=useState({
    fname:'',
    lname:'',
    });	
    


    const inputEvent = (event1) => {
      setfullname(event1.target.value);
    };

const onSubmit = (event) => {
event.preventDefault();
      setfullname(event.target.value);
      alert('formsubmitted');
    };


	return (
    <>
      <form onSubmit={onSubmit} action='#'>
          <h1>Your Full Name -  </h1>
          <input type="text" placeholder='Enter Your First Name' value={fullname.fname} onChange={inputEvent} name='fname'/>
          <input type="text" placeholder='Enter Your Last Name' value={fullname.lname} onChange={inputEvent} name='lname'/>
           <input type="text" placeholder='Enter Your Email'  value={fullname.email} onChange={inputEvent} name='email'/>
          <button type='submit'>Submit</button>
      </form>
    </>
	)
};
export default App;
