import React,{ useState } from 'react';
 

    

   const Main = () => {
 let [state,setState]=useState('vindo');
 let [fullname,setfullname]=useState();
 const getVal = (event) =>{

       setState(event.target.value);
    }
    const data = () =>{
setfullname(state);

    }
   	return(
       <>
       <form onSubmit={data}>
        <h1>hello...{fullname}</h1>
        <input type='text' placeholder='Enter Your Name...' onChange={getVal} val={state}/>
        <button type='submit'>Click me</button>
        </form>
       </>
   		)
   }

export default Main;
