import React,{useState} from 'react';

const fname = (event) =>{
console.log(event.target.value);	
}
const lname = (event1) =>{
console.log(event1.target.value);	
}
const onSubmit = (event2) =>{
console.log(event2.target.value);	
}
const App = () =>{
const [fname,setState]=useState();
    
	return(
    <>
      <form onSubmit={onSubmit}>
          <h1>Your Full Name - </h1>
          <input type="text" placeholder='Enter Your First Name' onChange={fname}/>
          <input type="text" placeholder='Enter Your Last Name' onChange={lname}/>
          <button type='submit'>Submit</button>
      </form>
    </>
	)
}
export default App;
