import React,{useState} from 'react';


const Lname = (event1) =>{
console.log(event1.target.value);	
}
const onSubmit = (event2) =>{
console.log(event2.target.value);	
}
const App = () =>{

    let [fname,setState]=useState('0');	
    const Fname = () =>{
setState(fname);
}
	return(
    <>
      <form onSubmit={onSubmit}>
          <h1>Your Full Name - {fname}  </h1>
          <input type="text" placeholder='Enter Your First Name' onChange={Fname}/>
          <input type="text" placeholder='Enter Your Last Name' onChange={Lname}/>
          <button type='submit'>Submit</button>
      </form>
    </>
	)
}
export default App;
