import React,{useState} from 'react';



const onSubmit = (event2) =>{
console.log(event2.target.value);	
}
const App = () =>{

    let [fname,setState]=useState('');	
     let [lname,setState1]=useState('');	
    const Fname = (event) =>{
      setState(event.target.value);
    }
    const Lname = (event1) =>{
setState1(event1.target.value);
}
	return(
    <>
      <form onSubmit={onSubmit}>
          <h1>Your Full Name - {fname} {lname}  </h1>
          <input type="text" placeholder='Enter Your First Name' onChange={Fname}/>
          <input type="text" placeholder='Enter Your Last Name' onChange={Lname}/>
          <button type='submit'>Submit</button>
      </form>
    </>
	)
}
export default App;
