import React,{useState} from 'react';




const App = () =>{

    let [fname,setState]=useState('');	
      let [lname,setState2]=useState('');	
    
       let [fullname,setState3]=useState('');	
        let [Lfullname,setState4]=useState('');	
       const onSubmit = (event) =>{
event.preventDefault();
setState3(fname);
setState4(lname);

}
    const Fname = (event1) =>{
      setState(event1.target.value);
    }

const Lname = (event) =>{
      setState2(event.target.value);
    }


	return(
    <>
      <form onSubmit={onSubmit}>
          <h1>Your Full Name - {fullname}{Lfullname}  </h1>
          <input type="text" placeholder='Enter Your First Name' value={fname} onChange={Fname}/>
          <input type="text" placeholder='Enter Your Last Name' onChange={lname} value={lname} onChange={Lname}/>
          <button type='submit'>Submit</button>
      </form>
    </>
	)
}
export default App;
